new class{constructor(){this.header=document.querySelector(".js-header"),this.humBtm=document.querySelector(".js-humBtn"),this.navLink=document.querySelectorAll(".js-nav a"),this.scorrllLinks=document.querySelectorAll('a[href^="/#"]'),this.btnHandler(),this.scroller()}btnHandler(){this.humBtm.addEventListener("click",(()=>this.header.classList.toggle("is-open")))}scroller(){this.scorrllLinks.forEach((e=>{e.addEventListener("click",(t=>{const s=e.getAttribute("href"),n=document.getElementById(s.replace("/#",""));if(null!=n){t.preventDefault();const e=n.getBoundingClientRect().top+window.pageYOffset-(this.header.classList.contains("is-open")?60:85);window.scrollTo({top:e,behavior:"smooth"}),window.addEventListener("scrollend",(()=>{this.header.classList.contains("is-open")&&this.header.classList.remove("is-open")}))}}))}))}};